import React, { useState } from "react";
import { getDocument } from "pdfjs-dist/build/pdf";
import "./Home.css";

export const Home = () => {
    const [transcript, setTranscript] = useState("");
    const [summary, setSummary] = useState("");
    const [tasks, setTasks] = useState("");
    const [followupEmail, setFollowupEmail] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleTranscriptChange = (e) => {
        setTranscript(e.target.value);
    };

    const handleFileChange = async (e) => {
        const file = e.target.files[0];
        if (file) {
            const fileType = file.type;

            if (fileType === "application/pdf") {
                // Handle PDF file
                const text = await readPdf(file);
                setTranscript(text); // Set the extracted text as the transcript
            } else {
                // Handle text file
                const reader = new FileReader();
                reader.onload = (event) => {
                    setTranscript(event.target.result); // Set the file content as the transcript
                };
                reader.readAsText(file); // Read the file as text
            }
        }
    };

    const readPdf = async (file) => {
        const pdf = await getDocument(URL.createObjectURL(file)).promise;
        let text = "";

        for (let i = 1; i <= pdf.numPages; i++) {
            const page = await pdf.getPage(i);
            const content = await page.getTextContent();
            const pageText = content.items.map((item) => item.str).join(" ");
            text += `${pageText}\n`; // Append text from each page
        }
        return text; // Return the combined text from all pages
    };

    const handleSubmit = async () => {
        setLoading(true);
        setError("");
        try {
            const response = await fetch('http://localhost:5002/summary', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ query: transcript })
            });
            const data = await response.json();
            setSummary(data.summary);
            setTasks(data.tasks);
            setFollowupEmail(data.followup_email);
        } catch (error) {
            setError("Error fetching data. Please try again later.");
            console.error("Error fetching summary:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="home-container">
            <h1>Meeting Extract</h1>
            <p>Summarize your meeting transcripts and extract tasks</p>
            <textarea
                value={transcript}
                onChange={handleTranscriptChange}
                placeholder="Paste your meeting transcript here or upload a document"
                rows="10"
                className="transcript-input"
            ></textarea>
            <div className="file-upload">
                <input 
                    type="file" 
                    accept=".txt,.pdf,.doc,.docx" 
                    onChange={handleFileChange} 
                    className="file-input"
                />
            </div>
            <button onClick={handleSubmit} className="submit-btn" disabled={loading}>
                {loading ? 'Processing...' : 'Submit'}
            </button>
            {error && <p className="error">{error}</p>}
            {summary && (
                <div className="summary-output">
                    <h3>Summary</h3>
                    <p>{summary}</p>
                </div>
            )}
            {tasks && (
                <div className="tasks-output">
                    <h3>Tasks</h3>
                    <p>{tasks}</p>
                </div>
            )}
            {followupEmail && (
                <div className="followup-output">
                    <h3>Follow-up Email</h3>
                    <p>{followupEmail}</p>
                </div>
            )}
        </div>
    );
};

export default Home;
