import React, { useState } from "react";
import "./Home.css";

export const Home = () => {
    const [transcript, setTranscript] = useState("");
    const [summary, setSummary] = useState("");
    const [tasks, setTasks] = useState("");
    const [followUpEmails, setFollowUpEmails] = useState("");
    const [loading, setLoading] = useState(false);  // Add loading state
    const [error, setError] = useState("");  // Add error state

    const handleTranscriptChange = (e) => {
        setTranscript(e.target.value);
    };

    const handleSubmit = async () => {
        setLoading(true);  // Set loading state to true when starting the request
        setError("");  // Reset any previous errors
        try {
            const response = await fetch('http://localhost:5002/summary', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ query: transcript })
            });
            const data = await response.json();
            //get summary, tasks and follow up emails from the data
            const { summary, tasks, followUpEmails } = data;
            setSummary(summary)
            setTasks(tasks)
            setFollowUpEmails(followUpEmails)
        } catch (error) {
            setError("Error fetching summary. Please try again later.");
            console.error("Error fetching summary:", error);
        } finally {
            setLoading(false);  // Set loading state back to false after request completes
        }
    };

    return (
        <div className="home-container">
            <h1>Meeting Extract</h1>
            <p>Summarize your meeting transcripts</p>
            <textarea
                value={transcript}
                onChange={handleTranscriptChange}
                placeholder="Paste your meeting transcript here"
                rows="10"
                className="transcript-input"
            ></textarea>
            <button onClick={handleSubmit} className="submit-btn" disabled={loading}>
                {loading ? 'Summarizing...' : 'Summarize'}
            </button>
            {error && <p className="error">{error}</p>}
            {summary && (
                <div className="summary-output">
                    <h3>Summary</h3>
                    <p>{summary}</p>
                </div>
            )}
            {tasks && (
                <div className="tasks-output">
                    <h3>Tasks</h3>
                    v

                </div>
            )}

            {followUpEmails && (
                <div className="follow-up-emails-output">
                    <h3>Follow-up Emails</h3>
                    <ul>
                        {followUpEmails.map((email, index) => (
                            <li key={index}>{email}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
