import React, { useState } from "react";
import "./Home.css";

export const Home = () => {
    const [transcript, setTranscript] = useState("");
    const [summary, setSummary] = useState("");
    const [tasks, setTasks] = useState("");
    const [followupEmail, setFollowupEmail] = useState("");

    const handleTranscriptChange = (e) => {
        setTranscript(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            const response = await fetch('http://localhost:5002/summary', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ query: transcript })
            });
            const data = await response.json();
            setSummary(data.summary);
            setTasks(data.tasks);
            setFollowupEmail(data.followup_email);  // Capture the follow-up email
        } catch (error) {
            console.error("Error fetching summary and follow-up email:", error);
        }
    };

    return (
        <div className="home-container">
            <h1>Meeting Extract</h1>
            <p>Summarize your meeting transcripts and generate a follow-up email</p>
            <textarea
                value={transcript}
                onChange={handleTranscriptChange}
                placeholder="Paste your meeting transcript here"
                rows="10"
                className="transcript-input"
            ></textarea>
            <button onClick={handleSubmit} className="submit-btn">Summarize</button>

            {summary && (
                <div className="summary-output">
                    <h3>Summary</h3>
                    <p>{summary}</p>
                </div>
            )}

            {tasks && (
                <div className="tasks-output">
                    <h3>Tasks</h3>
                    <p>{tasks}</p>
                </div>
            )}

            {followupEmail && (
                <div className="followup-email-output">
                    <h3>Follow-Up Email</h3>
                    <p>{followupEmail}</p>
                </div>
            )}
        </div>
    );
};

export default Home;
